# Structure for table "authentication"
#

DROP TABLE IF EXISTS `authentication`;
CREATE TABLE `authentication` (
  `user_id` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `authentication_key` varchar(64) NOT NULL COMMENT '认证key',
  `login_time` datetime NOT NULL COMMENT '登录时间',
  `login_ip` varchar(32) NOT NULL COMMENT '登录ip',
  `update_time` datetime NOT NULL COMMENT '最后刷新时间',
  PRIMARY KEY (`authentication_key`),
  UNIQUE KEY `authentication_key` (`authentication_key`),
  KEY `fk_a_user_id` (`user_id`),
  CONSTRAINT `fk_a_user_id` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户认证';

