DROP TABLE IF EXISTS `admin_log`;
CREATE TABLE `admin_log` (
  `log_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(10) NOT NULL COMMENT '用户id',
  `time` datetime NOT NULL COMMENT '日志记录时间',
  `ip` varchar(64) NOT NULL COMMENT 'ip',
  `url` varchar(128) NOT NULL DEFAULT '' COMMENT '操作的地址',
  `content` varchar(3000) NOT NULL DEFAULT '' COMMENT '日志内容',
  PRIMARY KEY (`log_id`),
  KEY `fk_log_user_id` (`user_id`),
  CONSTRAINT `fk_log_user_id` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=305 DEFAULT CHARSET=utf8 COMMENT='管理员操作日志';

