  `user_id` int(11) NOT NULL DEFAULT '0' COMMENT '所属用户',
  `is_super` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否为超级管理员',
  `add_date` datetime NOT NULL COMMENT '添加的时间',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `user_id` (`user_id`),
  CONSTRAINT `fk_user_id` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='管理员';

